    function payouts(uint256)
        external
        view
        returns (
            uint256 amount,
            uint256 remaining,
            uint256 endTiemstamp,
            uint256 ratio,
            address priorityPoolAddress
        );

    function policyCenter() external view returns (address);

    function shield() external view returns (address);
}
